import axios from 'axios';
import { Student, StudentCreateInput, StudentUpdateInput, StudentResponse, StudentsListResponse } from '../types/student';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export const studentServices = {
    // Get all students with pagination
    async getStudents(page: number = 1, limit: number = 10): Promise<StudentsListResponse> {
        try {
            const response = await axios.get(`${API_URL}/api/students`, {
                params: { page, limit },
                withCredentials: true
            });
            return response.data;
        } catch (error: any) {
            return {
                success: false,
                error: error.response?.data?.message || 'Failed to fetch students'
            };
        }
    },

    // Get a single student by ID
    async getStudentById(id: number): Promise<StudentResponse> {
        try {
            const response = await axios.get(`${API_URL}/api/students/${id}`, {
                withCredentials: true
            });
            return response.data;
        } catch (error: any) {
            return {
                success: false,
                error: error.response?.data?.message || 'Failed to fetch student'
            };
        }
    },

    // Create a new student
    async createStudent(studentData: StudentCreateInput): Promise<StudentResponse> {
        try {
            const response = await axios.post(`${API_URL}/api/students`, studentData, {
                withCredentials: true
            });
            return response.data;
        } catch (error: any) {
            return {
                success: false,
                error: error.response?.data?.message || 'Failed to create student'
            };
        }
    },

    // Update a student
    async updateStudent(studentData: StudentUpdateInput): Promise<StudentResponse> {
        try {
            const response = await axios.put(`${API_URL}/api/students/${studentData.id}`, studentData, {
                withCredentials: true
            });
            return response.data;
        } catch (error: any) {
            return {
                success: false,
                error: error.response?.data?.message || 'Failed to update student'
            };
        }
    },

    // Delete a student
    async deleteStudent(id: number): Promise<StudentResponse> {
        try {
            const response = await axios.delete(`${API_URL}/api/students/${id}`, {
                withCredentials: true
            });
            return response.data;
        } catch (error: any) {
            return {
                success: false,
                error: error.response?.data?.message || 'Failed to delete student'
            };
        }
    }
};
